mapper.select:
SELECT * FROM TAX
WHERE USERNO = #{userNo}
ORDER BY TAXNO DESC

mapper.update:
UPDATE TAX
TAXNO = #{taxNo}, USERNO = #{userNo}, PUBLISHDAY = #{publishDay}, BUSINESSCODE= #{businessCode}, BUSINESSNAME = #{businessName}, REGNO = #{regNo}, ITEM = #{item}, UNIT = #{unit}, QUANTITY = #{quantity}, UNITCOST = #{unitCost}, SUPPLYPRICE = #{supplyPrice}, TAXPRICE = #{taxPrice}
WHERE TAXNO = #{taxNo}
